import axios from "axios";
import {useState, useEffect, useMemo, SetStateAction} from "react"


export default function WeatherDisplay() {

    
    interface Data {
        [country: string]: string[]
    }

    // the city input and the day input
   // const [City, setCity] = useState(null)
    const [citiesfromapi, setCities] = useState(null)
 //   const [days, setDays] = useState("")
    const [a, setA] = useState({
        City: "",
        Days: ""
    })
    
    //the json data for the city validator
    const jsonarray = []
    const Cities = []

    //the renderers depending on the filters used.


    useEffect(() => {
        axios.get<Data | null>("https://raw.githubusercontent.com/russ666/all-countries-and-cities-json/master/countries.json")
            .then(res => jsonarray.push(res.data))
            .then(h => setCities(jsonarray))
    }, [])

    useEffect(() => {
        axios.get("http://localhost:5000")
            .then(res => console.log(res))
            .catch(err => console.log("request failed"))
    }, []) 

   /* const checker = () => {
        citiesfromapi && citiesfromapi.map(item => Cities.push(Object.keys(item)))
        
        console.log(Cities)
    }

    checker()*/

    const handleClick = (e: { preventDefault: () => void; }) => {
        e.preventDefault();
        axios.post("http://localhost:5000", a)
        }
        
    
  
    const handlechange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        setA({
            ...a,
            [e.target.name]: [e.target.value]
        })
    }

    return(
        <div>
        {a && <h1>{a.City}</h1>}
        <div className="flex max-h-full justify-center items-center bg-slate-600">
        <form>
           
            <input type="text" placeholder= "City" onChange={handlechange} />
            <input type="text" placeholder="Number of days" onChange={handlechange} />
            <button onClick= {handleClick} >Press here to send</button>
        </form>
        </div>
        </div>
    )
}
