import axios from "axios";
import {useState, useEffect, useMemo, SetStateAction} from "react"


export default function WeatherDisplay() {

    
    interface Data {
        [country: string]: string[]
    }

    // the city input and the day input
    const [City, setCity] = useState(null)
    const [citiesfromapi, setCities] = useState(null)
    const [days, setDays] = useState("")
    const [check, setCheck] = useState(false)

    
    //the json data for the city validator
    const jsonarray = []
    const Cities = []

    //the weather data receiver
    const [weather, setWeather] = useState<any>([])

    //the renderers depending on the filters used.


    // useEffect(() => {
    //     axios.get<Data | null>("https://raw.githubusercontent.com/russ666/all-countries-and-cities-json/master/countries.json")
    //          .then(res => jsonarray.push(res.data))
    //          .then(h => setCities(jsonarray))
    //         .then()
    // }, [])

    useEffect(() => {
        axios.get("https://raw.githubusercontent.com/dr5hn/countries-states-cities-database/master/countries%2Bcities.json")
            .then(res => setCities(res.data))
          //  .then(res2 => console.log(res2))
    }, [])

    useEffect(() => {
        axios.get("http://localhost:5000")
            .then(res => console.log(res))
            .catch(err => console.log("request failed"))
    }, []) 

    const checker = (city) => {
        citiesfromapi && citiesfromapi.map(item => {
            item.cities.map(item2 => {
                if(city === item2.name){
                    setCheck(true)
                } else{
                    console.log("That is not correct")
                }

            })
        })
    }

    

    //e: { preventDefault: () => void; }

    const data: {City: string, Days: string} = {
        City: City,
        Days: (days)
    }

    const handleClick = (e: { preventDefault: () => void; }) => {
        e.preventDefault();
        checker(data.City)
        if (data.City === "" || data.Days === ""){
            console.log("empty strings not allowed")
        } else if(check){
           console.log("that is a city")
           axios.post("http://localhost:5000", data)
          .then(res => setWeather(res.data))
          .then(res2 => console.log(weather))
 //         .then(res => setCity(""))
  //        .then(res2 => setDays(""))
          .catch(err => console.log(err))
        }}
  
    weather && console.log(weather)
    const handlechangeCity = (e: React.ChangeEvent<HTMLInputElement>) => {
        setCity(e.target.value)}

    const handlechangeDays = (e: React.ChangeEvent<HTMLInputElement>) => {
        setDays(e.target.value)
    }
    

    return(
        <div>
        <div className="flex max-h-full justify-center items-center bg-slate-600">
        <form>
            <input type="text" name="City"  placeholder= "City" onChange={handlechangeCity} />
            <input type="text" name="Days" placeholder="Number of days" onChange={handlechangeDays} />
            <button onClick= {handleClick} >Press here to send</button>
        </form>
        </div>
        </div>
    )
}
