import axios from "axios";
import {useState, useEffect, useMemo, SetStateAction} from "react"


export default function WeatherDisplay() {

    
    interface Data {
        [country: string]: string[]
    }

    // the city input and the day input and History input
    const [City, setCity] = useState(null)
    const [citiesfromapi, setCities] = useState(null)
    const [days, setDays] = useState("")
    const [check, setCheck] = useState(false)
    
    
    
    //the json data for the city validator
    const jsonarray = []
    const [Cities, setCitie] = useState(null)

    //the weather data receiver
    const [weather, setWeather] = useState<any>(null)
    const [theforecast, settheForecast] = useState(null)

    //the renderers depending on the filters used.
    const message = "Please enter a correct city!"

    //booleans for rendering different data
    const [current, setCurrent] = useState(false)
    const [Forecast, setForecast] = useState(false)

    //interface for post request data
    const data: { City: string, Days: string} = {
        City: City,
        Days: days,

    }

    //pushs all cities in world into array
    const filterer = Cities && Cities.filter(item => jsonarray.push(item.name))
    //returns true if city entered is actually a city.
    const checks = jsonarray.some(x => data.City === x)


    const timingofthecurrentday = weather && weather.forecast.forecastday.map(item => item.astro)
    const astronomicalInfo = timingofthecurrentday && timingofthecurrentday[1]


    useEffect(() => {
        axios.get("https://raw.githubusercontent.com/dr5hn/countries-states-cities-database/master/countries%2Bcities.json")
            .then(res => res.data)
            .then(res2 => setCitie(res2.reduce((prev, curr) => [...prev, ...curr.cities], [])))
    }, [])

    useEffect(() => {
        axios.get("http://localhost:5000")
            .then(res => console.log(res))
            .catch(err => console.log("request failed"))
    }, []) 

    const handleClick = (e: { preventDefault: () => void; }) => {
        e.preventDefault();
        if (data.City === "" && data.Days === ""){
            console.log("empty strings not allowed")}
        else{
            axios.post("http://localhost:5000", data)
           .then(res => setWeather(res.data))
           .catch(err => console.log(err))
        }
    
    }

    
    
  
    const handlechangeCity = (e: React.ChangeEvent<HTMLInputElement>) => {
        setCity(e.target.value)}

    const handlechangeDays = (e: React.ChangeEvent<HTMLInputElement>) => {
        setDays(e.target.value)
    }

    

    return(
        <>
        <div className="max-h-full justify-center items-center">
        <div className=" ">
        {checks === false ? "Enter the correct city" : null}
        <form>
            <input type="text" name="City" placeholder="City" onChange={handlechangeCity} className={checks === false ? "border-2 border-rose-600" : "border-2 border-green-600"}/>
            {Forecast && <input type="text" name="Days" placeholder="Number of days" onChange={handlechangeDays} />}
            <button onClick= {handleClick}>Press here to send</button>
        </form>
        </div>
        <div className="mb-9 min-h-[200px] block">
                <button className="block" onClick = {() => setForecast(false)}>Current Weather</button>
                <button onClick={() => {setForecast(true)}} className="block">Forecast</button>
        </div>
            { weather  && (Forecast === false) && (
                <div>
                <div className="weatherdisplay">
                <h1>City: {weather.location.name}</h1>
                <h2>Country: {weather.location.country}</h2>
                <p>Time: {weather.location.localtime}</p>
                </div>
                <div className="weatherInfo">
                <img src={weather.current.condition.icon} alt="representation of the condition" width="150" height="150"/>
                <h2>{weather.current.condition.text}</h2>
                <p>Temperature in Celsius: {weather.current.temp_c}</p>
                <p>Temperature in Fahrenheit: {weather.current.temp_f}</p>
                <p>Humidity: {weather.current.humidity}</p>
                </div>
                </div>
            )} 
            {weather && Forecast && (
                <div>
                       {weather && weather.forecast.forecastday.map(item => 
                       (
                           <div>
                           <h1>Date: {item.date}</h1>
                           <h2>{item.day.condition.text}</h2>
                           <img src={item.day.condition.icon} alt="representation of the condition" width="150" height="150"/>
                           <p>Peak temperature: {item.day.maxtemp_c}(Celcius)</p>
                            <p>Lowest temperature: {item.day.mintemp_c}(Celcius)</p>
                            <p>Chance of raining: {item.day.daily_chance_of_rain}%</p>
                           </div>
                       ))    
                       }

                </div>
            
            
            
            
            )}       
        </div>
        
            {astronomicalInfo && (
                <div>
                    <p>Sunrise: {astronomicalInfo.sunrise}</p>
                    <p>Sunset: {astronomicalInfo.sunset}</p>
                </div>
            )}
        
        </>
    )
}
