import axios from "axios";
import {useState, useEffect, useMemo, SetStateAction} from "react"

export default function WeatherDisplay() {

    interface Data{
        [country: string]: string[]
    }
    
    
    const [value, setValue] = useState("")
    const [cities, setCities] = useState<Data | null>(null)
    const jsonarray: (string|object)[] = []

    const handlechange =(e: { target: { value: SetStateAction<string>; }; }) => {
        setValue(e.target.value)
    }

    useEffect(() => {
        axios.get("http://localhost:5000")
            .then(res => console.log(res))
            .catch(err => console.log("request failed"))
    }, []) 

    const data: object = {
        text: value
    }

    const checker = () => {
        axios.get<Data | null>("https://raw.githubusercontent.com/russ666/all-countries-and-cities-json/master/countries.json")
        .then(res => console.log(res.data))
    } 

    checker()
    console.log(cities)

    const handleClick = (e: { preventDefault: () => void; }) => {
        e.preventDefault();
        axios.post("http://localhost:5000", data)
        .then(res => console.log("done"))
    }

    return(
        <>
        <h1>hello</h1>
        <form>
            <input type="text" onChange={handlechange} />
            <button onClick= {handleClick} >Press here to send</button>
        </form>
        </>
    )
}
