import axios from "axios";
import {useState, useEffect, useMemo, SetStateAction} from "react"
import City from "./City"

export default function WeatherDisplay() {

    
    interface Data {
        [country: string]: string[]
    }

    
    const [value, setValue] = useState("")
    const [citiesfromapi, setCities] = useState(null)
    const [days, setDays] = useState("")
    const jsonarray = []
    const Cities = []


    useEffect(() => {
        axios.get<Data | null>("https://raw.githubusercontent.com/russ666/all-countries-and-cities-json/master/countries.json")
            .then(res => jsonarray.push(res.data))
            .then(h => setCities(jsonarray))
    }, [])

    useEffect(() => {
        axios.get("http://localhost:5000")
            .then(res => console.log(res))
            .catch(err => console.log("request failed"))
    }, []) 

   /* const checker = () => {
        citiesfromapi && citiesfromapi.map(item => Cities.push(Object.keys(item)))
        
        console.log(Cities)
    }

    checker()*/

    const data: object = {
        City: value,
        Days: days
    }

    const handleClick = (e: { preventDefault: () => void; }) => {
        e.preventDefault();
        axios.post("http://localhost:5000", data)
        .then(res => console.log("done"))
    }
  
    const handlechangeCity = (e: { target: { value: SetStateAction<string>; }; }) => {
        setValue(e.target.value)
    }

    const handlechangeDays = (e: { target: { value: SetStateAction<string>; }; }) => {
        setDays(e.target.value)
    }

    return(
        <div className="flex max-h-full justify-center items-center">
        <h1>hello</h1>
        <form>
           
            <input type="text" placeholder= "City" onChange={handlechangeCity} />
            <input type="text" placeholder="Number of days" onChange={handlechangeDays} />
            <button onClick= {handleClick} >Press here to send</button>
        </form>
        </div>
    )
}
